import React, { useState, useEffect, useCallback } from 'react';
import {
  Box,
  Paper,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
  Alert,
  CircularProgress,
  Chip,
  Divider,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions
} from '@mui/material';
import moment from 'moment';
import CancelIcon from '@mui/icons-material/Cancel';
import ReceiptIcon from '@mui/icons-material/Receipt';
import { bookingAPI } from '../services/api';

const MyBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [cancelDialogOpen, setCancelDialogOpen] = useState(false);
  const [selectedBooking, setSelectedBooking] = useState(null);

  // Demo user ID - in real app, this would come from authentication
  const userId = `demo_user_123`;

  const fetchBookings = useCallback(async () => {
    try {
      setLoading(true);
      // For demo, we'll simulate some bookings since the backend might not have data
      const mockBookings = [
        {
          id: 1,
          booking_reference: 'BK000001',
          court: { name: 'Indoor Court 1', type: 'indoor' },
          coach: { name: 'John Smith', specialization: 'Advanced Training' },
          start_time: moment().add(1, 'day').set({ hour: 18, minute: 0 }).toISOString(),
          end_time: moment().add(1, 'day').set({ hour: 19, minute: 0 }).toISOString(),
          duration_hours: 1,
          court_price: 22.50,
          equipment_price: 13.00,
          coach_price: 30.00,
          total_price: 65.50,
          status: 'confirmed',
          payment_status: 'paid',
          BookingEquipment: [
            { Equipment: { name: 'Professional Racket' }, quantity: 2 },
            { Equipment: { name: 'Badminton Shoes' }, quantity: 1 }
          ]
        },
        {
          id: 2,
          booking_reference: 'BK000002',
          court: { name: 'Outdoor Court 2', type: 'outdoor' },
          coach: null,
          start_time: moment().add(2, 'days').set({ hour: 14, minute: 0 }).toISOString(),
          end_time: moment().add(2, 'days').set({ hour: 16, minute: 0 }).toISOString(),
          duration_hours: 2,
          court_price: 20.00,
          equipment_price: 5.00,
          coach_price: 0,
          total_price: 25.00,
          status: 'confirmed',
          payment_status: 'pending',
          BookingEquipment: [
            { Equipment: { name: 'Beginner Racket' }, quantity: 1 }
          ]
        }
      ];
      setBookings(mockBookings);

      // Uncomment for real API call:
      // const response = await bookingAPI.getUserBookings(userId);
      // setBookings(response.data);
    } catch (err) {
      setError('Failed to load bookings');
      console.error(err);
    } finally {
      setLoading(false);
    }
  }, [userId]);

  useEffect(() => {
    fetchBookings();
  }, [fetchBookings]);

  const handleCancelClick = (booking) => {
    setSelectedBooking(booking);
    setCancelDialogOpen(true);
  };

  const handleCancelConfirm = async () => {
    try {
      if (selectedBooking) {
        // Call API to cancel booking
        await bookingAPI.cancelBooking(selectedBooking.id);
        // Update local state
        setBookings(bookings.map(b => 
          b.id === selectedBooking.id 
            ? { ...b, status: 'cancelled' }
            : b
        ));
        setCancelDialogOpen(false);
        setSelectedBooking(null);
      }
    } catch (err) {
      setError('Failed to cancel booking');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'confirmed': return 'success';
      case 'cancelled': return 'error';
      case 'completed': return 'info';
      default: return 'default';
    }
  };

  const getPaymentStatusColor = (status) => {
    switch (status) {
      case 'paid': return 'success';
      case 'pending': return 'warning';
      case 'refunded': return 'info';
      default: return 'default';
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Paper sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        My Bookings
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }} onClose={() => setError(null)}>
          {error}
        </Alert>
      )}

      {bookings.length === 0 ? (
        <Box textAlign="center" py={6}>
          <Typography variant="h6" color="text.secondary" gutterBottom>
            No bookings found
          </Typography>
          <Button
            variant="contained"
            color="primary"
            href="/book"
            sx={{ mt: 2 }}
          >
            Make Your First Booking
          </Button>
        </Box>
      ) : (
        <Grid container spacing={3}>
          {bookings.map((booking) => (
            <Grid item xs={12} key={booking.id}>
              <Card>
                <CardContent>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
                    <Typography variant="h6">
                      Booking #{booking.booking_reference || `BK${String(booking.id).padStart(6, '0')}`}
                    </Typography>
                    <Box>
                      <Chip
                        label={booking.status.toUpperCase()}
                        color={getStatusColor(booking.status)}
                        size="small"
                        sx={{ mr: 1 }}
                      />
                      <Chip
                        label={booking.payment_status.toUpperCase()}
                        color={getPaymentStatusColor(booking.payment_status)}
                        size="small"
                      />
                    </Box>
                  </Box>

                  <Divider sx={{ my: 2 }} />

                  <Grid container spacing={3}>
                    <Grid item xs={12} md={6}>
                      <Typography variant="subtitle1" gutterBottom>
                        <strong>Court Details</strong>
                      </Typography>
                      <Typography variant="body1">
                        {booking.court?.name || 'Court'} ({booking.court?.type || 'N/A'})
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Date: {moment(booking.start_time).format('ddd, MMM D, YYYY')}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Time: {moment(booking.start_time).format('h:mm A')} - {moment(booking.end_time).format('h:mm A')}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Duration: {booking.duration_hours} hour(s)
                      </Typography>
                    </Grid>

                    <Grid item xs={12} md={6}>
                      <Typography variant="subtitle1" gutterBottom>
                        <strong>Add-ons</strong>
                      </Typography>
                      {booking.coach && (
                        <Typography variant="body2">
                          Coach: {booking.coach.name} (${booking.coach_price})
                        </Typography>
                      )}
                      {booking.BookingEquipment && booking.BookingEquipment.length > 0 && (
                        <>
                          <Typography variant="body2">
                            Equipment:
                          </Typography>
                          <Box component="ul" sx={{ pl: 2, mt: 0, mb: 1 }}>
                            {booking.BookingEquipment.map((item, idx) => (
                              <li key={idx}>
                                <Typography variant="body2">
                                  {item.Equipment?.name} x{item.quantity}
                                </Typography>
                              </li>
                            ))}
                          </Box>
                        </>
                      )}
                    </Grid>

                    <Grid item xs={12}>
                      <Divider sx={{ my: 1 }} />
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Typography variant="h6">
                          Total: ${booking.total_price}
                        </Typography>
                        <Box>
                          {booking.status === 'confirmed' && (
                            <Button
                              variant="outlined"
                              color="error"
                              startIcon={<CancelIcon />}
                              onClick={() => handleCancelClick(booking)}
                              sx={{ mr: 1 }}
                            >
                              Cancel Booking
                            </Button>
                          )}
                          <Button
                            variant="outlined"
                            startIcon={<ReceiptIcon />}
                          >
                            View Receipt
                          </Button>
                        </Box>
                      </Box>
                    </Grid>
                  </Grid>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}

      {/* Cancel Confirmation Dialog */}
      <Dialog
        open={cancelDialogOpen}
        onClose={() => setCancelDialogOpen(false)}
      >
        <DialogTitle>Cancel Booking</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to cancel booking #{selectedBooking?.booking_reference}?
            {selectedBooking?.payment_status === 'paid' && (
              <Alert severity="warning" sx={{ mt: 2 }}>
                Since this booking is already paid, a refund will be processed.
              </Alert>
            )}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCancelDialogOpen(false)}>
            Keep Booking
          </Button>
          <Button onClick={handleCancelConfirm} color="error" variant="contained">
            Cancel Booking
          </Button>
        </DialogActions>
      </Dialog>
    </Paper>
  );
};

export default MyBookings;
