import React, { useState, useEffect, useCallback } from 'react';
import {
  Box,
  Paper,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
  Alert,
  CircularProgress,
  Chip,
  Divider,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  IconButton,
  Tooltip
} from '@mui/material';
import moment from 'moment';
import CancelIcon from '@mui/icons-material/Cancel';
import ReceiptIcon from '@mui/icons-material/Receipt';
import RefreshIcon from '@mui/icons-material/Refresh';
import { bookingAPI } from '../services/api';

const MyBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [error, setError] = useState(null);
  const [cancelDialogOpen, setCancelDialogOpen] = useState(false);
  const [selectedBooking, setSelectedBooking] = useState(null);

  // Generate a demo user ID (in real app, this would come from auth context)
  const userId = localStorage.getItem('user_id') || `user_${Date.now().toString().slice(-6)}`;

  // Store user ID in localStorage for persistence
  useEffect(() => {
    if (!localStorage.getItem('user_id')) {
      localStorage.setItem('user_id', userId);
    }
  }, [userId]);

  const fetchBookings = useCallback(async () => {
    try {
      setLoading(true);
      const response = await bookingAPI.getUserBookings(userId);
      console.log('Bookings API response:', response.data);

      // Transform the data if needed
      const formattedBookings = response.data.map(booking => ({
        ...booking,
        start_time: new Date(booking.start_time),
        end_time: new Date(booking.end_time),
        created_at: new Date(booking.created_at),
        updated_at: new Date(booking.updated_at)
      }));

      setBookings(formattedBookings);
      setError(null);
    } catch (err) {
      console.error('Error fetching bookings:', err);

      // If API fails, show demo data
      if (err.response?.status === 404 || err.response?.status === 500) {
        const mockBookings = generateMockBookings(userId);
        setBookings(mockBookings);
        setError('Using demo data. Backend bookings API not available.');
      } else {
        setError('Failed to load bookings');
      }
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  }, [userId]);

  const generateMockBookings = (userId) => {
    const courts = [
      { id: 1, name: 'Indoor Court 1', type: 'indoor', base_price: 15.00 },
      { id: 2, name: 'Indoor Court 2', type: 'indoor', base_price: 15.00 },
      { id: 3, name: 'Outdoor Court 1', type: 'outdoor', base_price: 10.00 },
      { id: 4, name: 'Outdoor Court 2', type: 'outdoor', base_price: 10.00 }
    ];

    const coaches = [
      { id: 1, name: 'John Smith', specialization: 'Advanced Training', hourly_rate: 30.00 },
      { id: 2, name: 'Sarah Johnson', specialization: 'Beginner Coaching', hourly_rate: 25.00 },
      { id: 3, name: 'Mike Chen', specialization: 'Competitive Training', hourly_rate: 35.00 }
    ];

    const equipment = [
      { id: 1, name: 'Professional Racket', type: 'racket', price_per_session: 8.00 },
      { id: 2, name: 'Beginner Racket', type: 'racket', price_per_session: 5.00 },
      { id: 3, name: 'Badminton Shoes', type: 'shoes', price_per_session: 6.00 },
      { id: 4, name: 'Shuttlecocks', type: 'other', price_per_session: 12.00 }
    ];

    return [
      {
        id: 1,
        booking_reference: 'BK000001',
        user_id: userId,
        user_name: 'Demo User',
        user_email: 'demo@example.com',
        court: courts[0],
        coach: coaches[0],
        start_time: moment().add(1, 'day').set({ hour: 18, minute: 0 }).toDate(),
        end_time: moment().add(1, 'day').set({ hour: 19, minute: 0 }).toDate(),
        duration_hours: 1,
        court_price: 22.50,
        equipment_price: 13.00,
        coach_price: 30.00,
        total_price: 65.50,
        status: 'confirmed',
        payment_status: 'paid',
        created_at: moment().subtract(1, 'day').toDate(),
        updated_at: moment().subtract(1, 'day').toDate(),
        equipment_items: [
          { ...equipment[0], quantity: 2 },
          { ...equipment[2], quantity: 1 }
        ]
      },
      {
        id: 2,
        booking_reference: 'BK000002',
        user_id: userId,
        user_name: 'Demo User',
        user_email: 'demo@example.com',
        court: courts[2],
        coach: null,
        start_time: moment().add(2, 'days').set({ hour: 14, minute: 0 }).toDate(),
        end_time: moment().add(2, 'days').set({ hour: 16, minute: 0 }).toDate(),
        duration_hours: 2,
        court_price: 20.00,
        equipment_price: 5.00,
        coach_price: 0,
        total_price: 25.00,
        status: 'confirmed',
        payment_status: 'pending',
        created_at: moment().subtract(2, 'hours').toDate(),
        updated_at: moment().subtract(2, 'hours').toDate(),
        equipment_items: [
          { ...equipment[1], quantity: 1 }
        ]
      },
      {
        id: 3,
        booking_reference: 'BK000003',
        user_id: userId,
        user_name: 'Demo User',
        user_email: 'demo@example.com',
        court: courts[1],
        coach: coaches[1],
        start_time: moment().subtract(1, 'day').set({ hour: 10, minute: 0 }).toDate(),
        end_time: moment().subtract(1, 'day').set({ hour: 11, minute: 0 }).toDate(),
        duration_hours: 1,
        court_price: 18.00,
        equipment_price: 0,
        coach_price: 25.00,
        total_price: 43.00,
        status: 'completed',
        payment_status: 'paid',
        created_at: moment().subtract(3, 'days').toDate(),
        updated_at: moment().subtract(1, 'day').toDate(),
        equipment_items: []
      }
    ];
  };

  useEffect(() => {
    fetchBookings();
  }, [fetchBookings]);

  const handleRefresh = () => {
    setRefreshing(true);
    fetchBookings();
  };

  const handleCancelClick = (booking) => {
    setSelectedBooking(booking);
    setCancelDialogOpen(true);
  };

  const handleCancelConfirm = async () => {
    try {
      if (selectedBooking) {
        await bookingAPI.cancelBooking(selectedBooking.id);

        // Update local state
        setBookings(bookings.map(b => 
          b.id === selectedBooking.id 
            ? { ...b, status: 'cancelled', payment_status: 'refunded' }
            : b
        ));

        setCancelDialogOpen(false);
        setSelectedBooking(null);
        setError(null);
      }
    } catch (err) {
      setError('Failed to cancel booking');
      console.error('Cancel booking error:', err);
    }
  };

  const getStatusColor = (status) => {
    switch (status?.toLowerCase()) {
      case 'confirmed': return 'success';
      case 'cancelled': return 'error';
      case 'completed': return 'info';
      default: return 'default';
    }
  };

  const getPaymentStatusColor = (status) => {
    switch (status?.toLowerCase()) {
      case 'paid': return 'success';
      case 'pending': return 'warning';
      case 'refunded': return 'info';
      default: return 'default';
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2
    }).format(amount);
  };

  const isPastBooking = (booking) => {
    return moment(booking.end_time).isBefore(moment());
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Paper sx={{ p: 3 }}>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4">
          My Bookings
        </Typography>
        <Box>
          <Tooltip title="Refresh bookings">
            <IconButton onClick={handleRefresh} disabled={refreshing}>
              <RefreshIcon />
            </IconButton>
          </Tooltip>
          <Button
            variant="contained"
            color="primary"
            href="/book"
            sx={{ ml: 2 }}
          >
            Book New Court
          </Button>
        </Box>
      </Box>

      {error && (
        <Alert 
          severity={error.includes('demo data') ? 'info' : 'error'} 
          sx={{ mb: 3 }} 
          onClose={() => setError(null)}
        >
          {error}
        </Alert>
      )}

      {bookings.length === 0 ? (
        <Box textAlign="center" py={6}>
          <Typography variant="h6" color="text.secondary" gutterBottom>
            No bookings found
          </Typography>
          <Typography variant="body2" color="text.secondary" paragraph>
            You haven't made any bookings yet.
          </Typography>
          <Button
            variant="contained"
            color="primary"
            href="/book"
            sx={{ mt: 2 }}
          >
            Make Your First Booking
          </Button>
        </Box>
      ) : (
        <Grid container spacing={3}>
          {bookings.map((booking) => (
            <Grid item xs={12} key={booking.id}>
              <Card>
                <CardContent>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                    <Box>
                      <Typography variant="h6" gutterBottom>
                        Booking #{booking.booking_reference}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Booked on {moment(booking.created_at).format('MMM D, YYYY h:mm A')}
                      </Typography>
                    </Box>
                    <Box display="flex" alignItems="center" gap={1}>
                      {isPastBooking(booking) && (
                        <Chip
                          label="PAST"
                          color="default"
                          size="small"
                          variant="outlined"
                        />
                      )}
                      <Chip
                        label={booking.status?.toUpperCase() || 'UNKNOWN'}
                        color={getStatusColor(booking.status)}
                        size="small"
                      />
                      <Chip
                        label={booking.payment_status?.toUpperCase() || 'UNKNOWN'}
                        color={getPaymentStatusColor(booking.payment_status)}
                        size="small"
                      />
                    </Box>
                  </Box>

                  <Divider sx={{ my: 2 }} />

                  <Grid container spacing={3}>
                    <Grid item xs={12} md={6}>
                      <Typography variant="subtitle1" gutterBottom color="primary">
                        <strong>Court Details</strong>
                      </Typography>
                      <Box mb={2}>
                        <Typography variant="body1" fontWeight="medium">
                          {booking.court?.name} ({booking.court?.type})
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          Date: {moment(booking.start_time).format('dddd, MMMM D, YYYY')}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          Time: {moment(booking.start_time).format('h:mm A')} - {moment(booking.end_time).format('h:mm A')}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          Duration: {booking.duration_hours} hour(s)
                        </Typography>
                      </Box>

                      {booking.coach && (
                        <Box mb={2}>
                          <Typography variant="subtitle2" gutterBottom>
                            <strong>Coach:</strong> {booking.coach.name}
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            Specialization: {booking.coach.specialization}
                          </Typography>
                        </Box>
                      )}
                    </Grid>

                    <Grid item xs={12} md={6}>
                      <Typography variant="subtitle1" gutterBottom color="primary">
                        <strong>Price Breakdown</strong>
                      </Typography>
                      <Box mb={2}>
                        <Box display="flex" justifyContent="space-between" mb={1}>
                          <Typography variant="body2">Court Fee:</Typography>
                          <Typography variant="body2">{formatCurrency(booking.court_price)}</Typography>
                        </Box>
                        {booking.coach_price > 0 && (
                          <Box display="flex" justifyContent="space-between" mb={1}>
                            <Typography variant="body2">Coach Fee:</Typography>
                            <Typography variant="body2">{formatCurrency(booking.coach_price)}</Typography>
                          </Box>
                        )}
                        {booking.equipment_price > 0 && (
                          <Box display="flex" justifyContent="space-between" mb={1}>
                            <Typography variant="body2">Equipment:</Typography>
                            <Typography variant="body2">{formatCurrency(booking.equipment_price)}</Typography>
                          </Box>
                        )}
                        <Divider sx={{ my: 1 }} />
                        <Box display="flex" justifyContent="space-between">
                          <Typography variant="body1" fontWeight="medium">Total:</Typography>
                          <Typography variant="body1" fontWeight="medium">{formatCurrency(booking.total_price)}</Typography>
                        </Box>
                      </Box>

                      {booking.equipment_items && booking.equipment_items.length > 0 && (
                        <Box>
                          <Typography variant="subtitle2" gutterBottom>
                            <strong>Equipment Booked:</strong>
                          </Typography>
                          {booking.equipment_items.map((item, idx) => (
                            <Typography key={idx} variant="body2" color="text.secondary">
                               {item.name} x{item.quantity} (+{formatCurrency(item.price_per_session * item.quantity)})
                            </Typography>
                          ))}
                        </Box>
                      )}
                    </Grid>

                    <Grid item xs={12}>
                      <Divider sx={{ my: 1 }} />
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', pt: 2 }}>
                        <Typography variant="body2" color="text.secondary">
                          Booking ID: {booking.id}
                        </Typography>
                        <Box>
                          {booking.status === 'confirmed' && !isPastBooking(booking) && (
                            <Button
                              variant="outlined"
                              color="error"
                              startIcon={<CancelIcon />}
                              onClick={() => handleCancelClick(booking)}
                              sx={{ mr: 1 }}
                            >
                              Cancel Booking
                            </Button>
                          )}
                          <Button
                            variant="outlined"
                            startIcon={<ReceiptIcon />}
                            onClick={() => {
                              // Show receipt/download invoice
                              alert(`Receipt for Booking #${booking.booking_reference}\nTotal: ${formatCurrency(booking.total_price)}`);
                            }}
                          >
                            View Receipt
                          </Button>
                        </Box>
                      </Box>
                    </Grid>
                  </Grid>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}

      {/* Cancel Confirmation Dialog */}
      <Dialog
        open={cancelDialogOpen}
        onClose={() => setCancelDialogOpen(false)}
      >
        <DialogTitle>Cancel Booking</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to cancel booking #{selectedBooking?.booking_reference}?
            <br /><br />
            <strong>Court:</strong> {selectedBooking?.court?.name}
            <br />
            <strong>Date:</strong> {selectedBooking?.start_time ? moment(selectedBooking.start_time).format('MMM D, YYYY') : ''}
            <br />
            <strong>Time:</strong> {selectedBooking?.start_time ? moment(selectedBooking.start_time).format('h:mm A') : ''}
            <br /><br />
            {selectedBooking?.payment_status === 'paid' && (
              <Alert severity="warning" sx={{ mt: 1 }}>
                Since this booking is already paid, a refund will be processed to your original payment method.
              </Alert>
            )}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCancelDialogOpen(false)}>
            Keep Booking
          </Button>
          <Button 
            onClick={handleCancelConfirm} 
            color="error" 
            variant="contained"
            startIcon={<CancelIcon />}
          >
            Cancel Booking
          </Button>
        </DialogActions>
      </Dialog>
    </Paper>
  );
};

export default MyBookings;
